------------------------------- PRODUTOCREATE TABLE orderfood.produto (  id INTEGER NOT NULL,  nome VARCHAR(60) NOT NULL,  descricao VARCHAR(80),  urlfoto VARCHAR(50),  volume INTEGER,  valor NUMERIC(12,6),  qtestoque INTEGER,  categoria VARCHAR(50),  CONSTRAINT produto_pkey PRIMARY KEY(id)) WITH (oids = false);   --------------------------------- CABPEDIDOCREATE TABLE orderfood.cabpedido (  numped INTEGER NOT NULL,  datacriacao DATE,  valordesconto NUMERIC(12,6),  valortotal NUMERIC(12,6),  observacao VARCHAR(60),  dataentrega DATE,  status VARCHAR(15),  PRIMARY KEY(numped)) WITH (oids = false);    ------------------------------ 	ITEMPEDIDOCREATE TABLE orderfood.itempedido (  numped INTEGER NOT NULL,  produto INTEGER NOT NULL,  quantidade INTEGER NOT NULL,  valorunitario INTEGER NOT NULL,  CONSTRAINT itempedido_pkey PRIMARY KEY(numped, produto)) WITH (oids = false);CREATE UNIQUE INDEX uniq_ped ON orderfood.itempedido  USING btree (numped, produto);  ALTER TABLE orderfood.itempedido  ADD CONSTRAINT itempedido_fk FOREIGN KEY (produto)  REFERENCES orderfood.produto(id)    ON DELETE NO ACTION    ON UPDATE NO ACTION    NOT DEFERRABLE;       