CREATE SEQUENCE produto_produto_id_seq  INCREMENT 1  MINVALUE 1  MAXVALUE 9223372036854775807  START 1  CACHE 1;------------------------------- PRODUTOCREATE TABLE orderfood.produto (  id INTEGER NOT NULL DEFAULT NEXTVAL('produto_produto_id_seq'),  nome VARCHAR(60) NOT NULL,  descricao VARCHAR(80) NOT NULL,  urlfoto VARCHAR(50),  volume NUMERIC(12,2),  valor NUMERIC(12,2) NOT NULL,  unidade VARCHAR(20) NOT NULL,  status BOOLEAN DEFAULT TRUE,  qtestoque INTEGER NOT NULL,  categoria VARCHAR(20) NOT NULL,  CONSTRAINT produto_id PRIMARY KEY(id)) WITH (oids = false);--------------------------------- CABPEDIDOCREATE TABLE orderfood.cabpedido (  numped INTEGER NOT NULL,  datacriacao DATE,  valordesconto NUMERIC(12,6),  valortotal NUMERIC(12,6),  observacao VARCHAR(60),  dataentrega DATE,  status VARCHAR(15),  PRIMARY KEY(numped)) WITH (oids = false);    ------------------------------ 	ITEMPEDIDOCREATE TABLE orderfood.itempedido (  numped INTEGER NOT NULL,  produto INTEGER NOT NULL,  quantidade INTEGER NOT NULL,  valorunitario INTEGER NOT NULL,  CONSTRAINT itempedido_pkey PRIMARY KEY(numped, produto)) WITH (oids = false);CREATE UNIQUE INDEX uniq_ped ON orderfood.itempedido  USING btree (numped, produto);  ALTER TABLE orderfood.itempedido  ADD CONSTRAINT itempedido_fk FOREIGN KEY (produto)  REFERENCES orderfood.produto(id)    ON DELETE NO ACTION    ON UPDATE NO ACTION    NOT DEFERRABLE;                                   